const i=document.getElementById("hamburger"),e=document.getElementById("fullscreen-menu"),n=document.body;i?.addEventListener("click",function(){e&&(e.classList.remove("invisible"),setTimeout(()=>{e.style.opacity="1",n.style.overflow="hidden"},10))});e?.addEventListener("click",function(t){t.target&&t.target.id==="fullscreen-menu"&&(e.style.opacity="0",setTimeout(()=>{e.classList.add("invisible"),n.style.overflow=""},300))});
